import React from "react";
import { Link } from "react-router-dom";
import { FaArrowRight } from "react-icons/fa";

const Bikes = ({ bikesData }) => {
    return (
      <div className="vehicle-container">
        <h2>Bikes</h2>
        <div className="vehicle-grid">
          {bikesData.map((bike) => (
            <div className="vehicle-card" key={bike.id}>
              <img src={bike.image} alt="bike" />
              <h3>{bike.name}</h3>
              <p>Price: {bike.price} KES</p>
              {/* Update the Link to include the type and id */}
              <Link to={`/booking/${bike.type}/${bike.id}`} className="btn">
                Book Now <FaArrowRight size={12} />
              </Link>
            </div>
          ))}
        </div>
      </div>
    );
  };

export default Bikes;
