import React, { useState, useEffect } from "react";
import Bikes from "./Bikes";
import Scooters from "./Scooters";
import "./Styles.css";

const BikesAndScooters = () => {
    const [bikes, setBikes] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);

    useEffect(() => {
        fetch('https://e-bikes.onrender.com/Bikes')
            .then((resp) => resp.json())
            .then((data) => setBikes(data))
            .catch((error) => {
                console.error('Error fetching data:', error);
                setBikes([]);
            });
    }, []);

    const bikesData = bikes.slice(0, 7);
    const scootersData = bikes.slice(7, 16);

    const handleUserClick = (user) => {
        setSelectedUser(user);
    };

    return (
        <>
            <p>
                <Link to="/bikes">Back to Bicycles</Link>
            </p>
            <div className="bikes-and-scooters">
                <div className="bikes-section">
                    <h2>Bicycles</h2>
                    <p>
                        Our bicycles are designed to provide a smooth and comfortable ride for
                        all types of cyclists. Whether you're commuting to work, exploring the
                        city, or enjoying a leisurely ride in the park, our bikes are perfect
                        companions for your journey. With sturdy frames, reliable brakes, and
                        adjustable seats, our bicycles offer the best in safety and performance.
                    </p>
                    <Bikes bikesData={bikesData} />
                </div>
                <div className="scooters-section">
                    <h2>Scooters</h2>
                    <p>
                        Experience the thrill of riding our electric scooters! Effortlessly
                        glide through the streets with our eco-friendly and convenient scooters.
                        The sleek design and smooth handling make these scooters a popular choice
                        for urban commuters and tourists alike. Our scooters come equipped with
                        powerful batteries and comfortable handles, ensuring an enjoyable ride
                        every time.
                    </p>
                    <Scooters scootersData={scootersData} />
                </div>
            </div>
        </>
    );
};

export default BikesAndScooters;
